import React, {createContext, useReducer} from 'react';
import {AppReducer} from './AppReducer';

//initial state
const initialState ={
    transactions:[
        {id:1, text: 'flower', amount: -40},
        {id:2, text: 'salary', amount: -200},
        {id:3, text: 'book', amount: -2000},
        {id:4, text: 'camera', amount: -10},
        {id:5, text: 'stuff', amount: -30},
    ]
}

//create context
export const GlobalState = createContext(initialState);

//provider component
export const GlobalProvider = ({children}) => {
    const [state,dispath] = useReducer(AppReducer,initialState);

    return (<GlobalProvider.Provider value={{
        transactions: state.transactions
    }}>
    {children}
    </GlobalProvider.Provider>);
}
